(21, 'BÌNH LỌC NƯỚC MÁY PHUN SƯƠNG', 'binh-loc-nuoc-may-phun-suong', 1, 'images/uploads/images/sanpham/binh-loc-may-phun-suong.jpg', NULL, '<h1 itemprop="name">\r\n	<span style="font-size:16px;">Bình lọc nước máy phun sương&nbsp; giúp nước qua hệ thống phun sương sạch hơn chống tắc béc phun bảo vệ hệ thống phun bền hơn.&nbsp;<strong>Bình lọc nước phun sương</strong>&nbsp;được sử dụng rộng rãi trong việc lắp đặt hệ thống máy phun sương làm mát không khí, tạo ẩm cho không gian xung quanh được mát mẻ, thoải mái dễ chịu cho khách hàng. Lắp đặt hệ thống phun sương cho văn phòng, sân vườn, quán cà phê, xưởng công nghiệp với nhiều ưu đãi lớn tại cửa hàng máy phun sương Khánh Xuân.</span>\r\n</h1>\r\n\r\n<p style="text-align: center;">\r\n	<span style="font-size:16px;"><img alt="" src="http://phunsuongcongnghe.com/images/uploads/images/sanpham/binh-loc-may-phun-suong.jpg" style="width: 50%; height: 50%;"></span>\r\n</p>\r\n\r\n<p>\r\n	&nbsp;\r\n</p>', 'BÌNH LỌC NƯỚC MÁY PHUN SƯƠNG', '<p>\r\n	BÌNH LỌC NƯỚC MÁY PHUN SƯƠNG\r\n</p>', 'BÌNH LỌC NƯỚC MÁY PHUN SƯƠNG, máy phun sương, may phun suong', 'product.index', '150000', 0, '0', 1, 1, 2, '2018-03-16 03:24:27', '2018-03-20 09:28:21'),
(22, 'Bộ Béc Phun Sương Cuối 3 Đầu Phun Đài Loan', 'bo-bec-phun-suong-cuoi-3-dau-phun-dai-loan', 1, 'images/uploads/images/sanpham/may-phun-3-dau.jpg', NULL, '<h1 itemprop="name">\r\n	<span style="font-size:16px;">Đầu tưới cây phun sương tạo nên một chùm nước hạt sương, hạt nước nhỏ mịn thích hợp cây trồng trong chậu, tưới lan, làm mát không gian sân vườn. Thích hợp cho cây trồng nhà phố, vườn ươm và tưới lan, nấm&hellip;giúp cho không gian xung quanh khách hàng mát mẻ dễ chịu và giá rẻ</span>\r\n</h1>\r\n\r\n<p style="text-align: center;">\r\n	<span style="font-size:16px;"><img alt="" src="http://phunsuongcongnghe.com/images/uploads/images/sanpham/may-phun-3-dau.jpg" style="width: 600px; height: 600px;"></span>\r\n</p>\r\n\r\n<p>\r\n	Thông số kỹ thuật&nbsp;Bộ béc phun sương&nbsp;cuối 3 đầu phun:\r\n</p>\r\n\r\n<p>\r\n	Có thề lắp dặt đầu tưới theo 2 cách : Phun từ dưới lên hoặc phun phủ từ trên xuống\r\n</p>\r\n\r\n<p>\r\n	&ndash; Đầu tưới chịu được tia UV tốt nên có độ bền cao.\r\n</p>\r\n\r\n<p>\r\n	&ndash; Kết nối bền\r\n</p>\r\n\r\n<p>\r\n	&ndash; Nhỏ gọn\r\n</p>\r\n\r\n<p>\r\n	&ndash; Trọn bộ giá rẻ\r\n</p>\r\n\r\n<p>\r\n	&ndash; Lắp đặt nhanh\r\n</p>\r\n\r\n<p>\r\n	Ví trị lắp đặt Bộ béc phun sương cuối 3 đầu phun\r\n</p>\r\n\r\n<p>\r\n	Do có đường kính từ 0.8-1m nên có thể lắp từ trên phun sương mịn xuống, hoắc từ dưới phua tỏa ra, ngoài ra đầu phun sương chum dễ dàng tháo lắp nên dễ dàng thay đổi cách bố trí theo từng loại cây trồng.\r\n</p>', 'Bộ Béc Phun Sương Cuối 3 Đầu Phun Đài Loan', '<p>\r\n	Bộ Béc Phun Sương Cuối 3 Đầu Phun Đài Loan\r\n</p>', 'Bộ Béc Phun Sương Cuối 3 Đầu Phun Đài Loan, máy phun sương, may phun suong', 'product.index', '90000', 0, '0', 1, 1, 2, '2018-03-16 03:24:27', '2018-03-20 09:30:20');
